<?php	class SignupGateway	{		private PDO $conn;				public function __construct(DataBase $database)		{			$this->conn = $database->connection();						if(! $this->conn){				echo("Error: MySQL Server Connection Failed!");			}		}		public function postAll(array $data): array		{			$sql = "INSERT INTO person					VALUES('$data[0]', '$data[1]', $data[2], '$data[3]', '$data[4]', '$data[5]',					'$data[6]', '$data[7]', '$data[8]', '$data[9]', $data[10], $data[11],					$data[12], $data[13], '$data[14]', '$data[15]', '$data[16]', $data[17],					'$data[18]', '$data[19]')";								$stmt = $this->conn->prepare($sql);						$stmt->execute();						$data = $stmt->fetch(PDO::FETCH_ASSOC);						if(!$data)			{				$data = ["Success", $stmt->rowCount()];			}						return $data;		}	}?>